  runtime {
    environmentEnvironmentalGoal(
      searchFilter: {
        type: ATTRIBUTE_FILTER
        searchTerm: $item
        attributeNames: ["name"]
      }
    ) {
      items {
        name
        goalText
        goalStatus
      }
    }
  }
}

