url_options.body_graphql_query:
query getEnergyMeterPower($item: OctoObjectId!) {
  runtime {
    industryEnergyEnergyMeter(rtId: $item) {
      items {
        name
        rtId
        power
        voltage
        ampere
      }
    }
  }
}

query {
  streamData {
    industryEnergyEnergyMeter(
      arg: {
        limit: 100
        from: "${__from:date}"
        to: "${__to:date}"
        queryMode: DOWNSAMPLING
      }
      rtId: "${_CurrentPowerId}"
    ) {
      items {
        rtId
        ckTypeId
        power(arg: {aggregationType: AVG})
        timeStamp
      }
    }
  }
}

